let layout = 0;

$("#layout").click(function() {

	if (layout === 0) {

		$("#settings_choose").slideUp(500)
		$(".layout_choose").show(500)
		$("#layout_tetx").html("Settings")
		$("#settings").html("Ready layouts")
		$(".layout").css("left", "225px")

		layout = 1;

	} else if (layout === 1) {

		$("#settings_choose").show(500)
		$(".layout_choose").slideUp(500)
		$("#layout_tetx").html("Ready layouts")
		$("#settings").html("Settings")
		$(".layout").css("left", "260px")

		layout = 0;

	}

	

})

